  //     return <Load/>;
  //   }

  //   return <div>Ready!</div>;
  // }

  state = {  display: false  }

  handle = () => {
    this.setState({ display: true })
  }


  renderMap() {

    return (
      <div className="App">

        <Header />

        <Content />

      </div>

    );
  }

  renderWaitGetData() {
    

    if (this.state.display == true) {
      return this.renderMap();
    }
    else
      return (
        <div onClick={this.handle}>
          <Load />
        </div>
      );
  }

  render() {
    return <div>{this.renderWaitGetData()}</div>;
  }

}

export default App;
